mapper.select:
SELECT IDX , USER_IDX , NOW_POINT , OPTIONS , CREATE_DTTM , UPDATE_DTTM FROM POINT_SUMMARY WHERE 1=1
AND USER_IDX = #{userIdx}
ORDER BY IDX, USER_IDX, UPDATE_DTTM

SELECT IDX , USER_IDX , STATUS , POINT , OPTIONS , CREATE_DTTM , UPDATE_DTTM , EXPIRATION_DTTM FROM POINT_EVENT WHERE 1=1
AND USER_IDX = #{userIdx}
ORDER BY IDX, USER_IDX, CREATE_DTTM

mapper.insert:
INSERT INTO POINT_EVENT (
	USER_IDX
	, STATUS
	, POINT
	, OPTIONS
	, CREATE_DTTM
	, UPDATE_DTTM
	, EXPIRATION_DTTM
) VALUES (
	#{userIdx}
	, #{status}
	, #{point}
	, #{options}
	, #{createDttm}
	, #{updateDttm}
	, #{expirationDttm}
)

INSERT INTO POINT_EVENT_DETAIL (
	USER_IDX
	, EVENT_IDX
	, ORIGIN_EVENT_DETAIL_IDX
	, STATUS
	, POINT
	, OPTIONS
	, CREATE_DTTM
	, UPDATE_DTTM
) VALUES (
	#{userIdx}
	, #{eventIdx}
	, #{originEventDetailIdx}
	, #{status}
	, #{point}
	, #{options}
	, #{createDttm}
	, #{updateDttm}
)

mapper.update:
UPDATE POINT_SUMMARY 
SET NOW_POINT = (
	SELECT SUM(POINT) 
	FROM POINT_EVENT_DETAIL 
	WHERE USER_IDX = #{userIdx}
)
WHERE USER_IDX = #{userIdx}

mapper.select:
SELECT NOW_POINT
FROM POINT_SUMMARY
WHERE USER_IDX = #{userIdx}
